mapper.select:
SELECT count(*) from tju_item_order WHERE 1 = 1

AND name LIKE concat('%', #{name, jdbcType=VARCHAR}, '%')


AND provider LIKE concat('%', #{provider, jdbcType=VARCHAR}, '%')



  AND id = #{id, jdbcType=INTEGER}



AND time >= #{mintime, jdbcType=TIMESTAMP}


AND time <= #{maxtime, jdbcType=TIMESTAMP}


AND customer LIKE concat('%', #{customer, jdbcType=VARCHAR}, '%')

  SELECT * FROM tju_item_order WHERE 1=1

  AND name LIKE concat('%', #{name, jdbcType=VARCHAR}, '%')


  AND customer LIKE concat('%', #{customer, jdbcType=VARCHAR}, '%')


  AND provider LIKE concat('%', #{provider, jdbcType=VARCHAR}, '%')



    AND id = #{id, jdbcType=INTEGER}



  AND time >= #{mintime, jdbcType=TIMESTAMP}


  AND time <= #{maxtime, jdbcType=TIMESTAMP}

limit #{start, jdbcType=INTEGER},#{end, jdbcType=INTEGER}


select id, name, count, price, total, customer, time, provider, phone, address, info,sdes
  from tju_item_order
where id = #{id,jdbcType=INTEGER}
