mapper.select:

select id, name, count, price, total, sdes, time, provider
from tju_provider_order
where id = #{id,jdbcType=INTEGER}

SELECT count(*) from tju_provider_order WHERE 1 = 1

    AND name LIKE concat('%', #{name, jdbcType=VARCHAR}, '%')


    AND provider LIKE concat('%', #{provider, jdbcType=VARCHAR}, '%')


    AND sdes LIKE concat('', #{sdes, jdbcType=VARCHAR}, '')



        AND id = #{id, jdbcType=INTEGER}



      AND time >= #{mintime, jdbcType=TIMESTAMP}


    AND time <= #{maxtime, jdbcType=TIMESTAMP}

SELECT * FROM tju_provider_order WHERE 1=1

    AND name LIKE concat('%', #{name, jdbcType=VARCHAR}, '%')


    AND provider LIKE concat('%', #{provider, jdbcType=VARCHAR}, '%')


    AND sdes LIKE concat('', #{sdes, jdbcType=VARCHAR}, '')



        AND id = #{id, jdbcType=INTEGER}



    AND time >= #{mintime, jdbcType=TIMESTAMP}


    AND time <= #{maxtime, jdbcType=TIMESTAMP}

limit #{start, jdbcType=INTEGER},#{end, jdbcType=INTEGER}
